function set(key, value) {
  if (typeof key != "string") {
    throw "Typeof key in function set must have a typeof string (Hint: Check the typeof the input before sending it to this function)";
  } else {
    sessionStorage.setItem(key, value);
  }
}
function get(key) {
  if (typeof key != "string") {
    throw "Typrof key in function get must have a typeof string (Hint: Check the typeof the input before sending it to this function)";
  } else {
    sessionStorage.getItem(key);
  }
}
function getObjectValues(object, startpoint) {
  var txt = "";
  if (startpoint == undefined) {
    startpoint = 0;
  }
  for (; startpoint < object.length; startpoint++) {
    txt += object[startpoint] + " ";
  }
  return txt;
}
function hexafy(str) {
  return str.toString(16);
}
function bitafy(str) {
  return str.toString(2);
}
